    private void SetResultActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SetResultActionPerformed
          
        CompuertasLogicas obj = new CompuertasLogicas((String)jCmb_Option_Operator.getSelectedItem(),
                Value_left.getSelectedIndex(), Value_right.getSelectedIndex());
        Result.setText(obj.makeoperation());        
    }//GEN-LAST:event_SetResultActionPerformed

