    private void jCmb_Option_OperatorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCmb_Option_OperatorActionPerformed
        if (jCmb_Option_Operator.getSelectedItem().equals("NOT")) {
            JOptionPane.showMessageDialog(null, "No se puede validar ", "¡Advertencia!",JOptionPane.INFORMATION_MESSAGE);
            Value_right.setVisible(false);
        }else{
            Value_right.setVisible(true);
        }
    }//GEN-LAST:event_jCmb_Option_OperatorActionPerformed

